import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';

function ShareFilesButton({ username }) {
  const [sharedLink, setSharedLink] = useState('');

  const handleShareFiles = async () => {
    try {
      const response = await fetch(`https://2iwjhqhn3m.execute-api.us-east-1.amazonaws.com/resumelist/?username=${username}`);
      const data = await response.json();
      // Assuming data is an array of users
      // Modify the logic as per your response structure
      setSharedLink(JSON.stringify(data));
      // Copy the shared link to the clipboard
      await navigator.clipboard.writeText(`https://2iwjhqhn3m.execute-api.us-east-1.amazonaws.com/resumelist?username=${username}`);
    } catch (error) {
      console.error('Error sharing files:', error);
    }
  };

  return (
    <div>
      <Button variant="primary" onClick={handleShareFiles}>Share Files</Button>
      {sharedLink && (
        <div>
          <h2>Shared Files:</h2>
          <p>{sharedLink}</p>
        </div>
      )}
    </div>
  );
}

export default ShareFilesButton;
