import React, { useState } from 'react';

const API_ENDPOINT = "https://2iwjhqhn3m.execute-api.us-east-1.amazonaws.com/meow";

function Display() {
  const [profileData, setProfileData] = useState([]);
  const [error, setError] = useState(null);

  const handleGetProfile = () => {
    fetch(API_ENDPOINT)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setProfileData(data);
        setError(null); // Reset error state if request is successful
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
        setError(error.message); // Set error state with the error message
      });
  };

  return (
    <div>
      <input type="button" id="getprofile" value="View all the Student Resumes" onClick={handleGetProfile} />
      <br />
      {error && <div>Error: {error}</div>}
      <div id="showProfile">
        <table id="Profile">
          <colgroup>
            <col style={{ width: '20%' }} />
            <col style={{ width: '20%' }} />
            <col style={{ width: '20%' }} />
            <col style={{ width: '20%' }} />
          </colgroup>
          <tbody>
            <tr>
              <th>Student ID</th>
              <th>Uploaded Time</th>
              <th>File Link</th>
            </tr>
            {profileData.map(profile => (
              <tr key={profile.ID}>
                <td>{profile.ID}</td>
                <td>{profile.fileId}</td>
                <td>{profile.uploadedAt}</td>
                <td><a href={profile.s3Location}>{profile.fileId}</a></td>
              
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Display;
