// ItemApi.js

import axios from 'axios';

const BASE_URL = 'https://r5hryly90j.execute-api.ap-south-1.amazonaws.com/items';

const ItemApi = {
  createOrUpdateItem: async (itemData) => {
    try {
      const response = await axios.put(BASE_URL, itemData, {
        headers: { 'Content-Type': 'application/json' },
      });
      return response.data;
    } catch (error) {
      console.error('Error creating/updating item:', error);
      throw error;
    }
  },

  getAllItems: async () => {
    try {
      const response = await axios.get(BASE_URL);
      return response.data;
    } catch (error) {
      console.error('Error getting all items:', error);
      throw error;
    }
  },

  getItemById: async (itemId) => {
    try {
      const response = await axios.get(`${BASE_URL}/${itemId}`);
      return response.data;
    } catch (error) {
      console.error(`Error getting item with ID ${itemId}:`, error);
      throw error;
    }
  },

  deleteItem: async (itemId) => {
    try {
      await axios.delete(`${BASE_URL}/${itemId}`);
    } catch (error) {
      console.error(`Error deleting item with ID ${itemId}:`, error);
      throw error;
    }
  },
};

export default ItemApi;
